pair* hashtable[100];

int hash(char* str) {
    int hash = 0;
    int len = strlen(str);
    for (int i = 0; i < len; i++) {
        hash += str[i]; 
        hash = (hash * str[i]) % 100;
    }   
    return hash;
}

void insert(char* key, char* value) {
    int index = hash(key);
    pair* p = malloc(sizeof(pair));
    p->key = key; 
    p->value = value;
    hashtable[index] = p;
}

void* readFile(char* filename) {
    FILE* f = fopen(filename, "rb");
    if (!f) return NULL;

    unsigned char* buffer = malloc(BUFSIZE);
    size_t len; 

    fread(buffer, 1, BUFSIZE, f);
    while (!feof(f)) {
        len = fread(buffer, 1, BUFSIZE, f);
        fread(buffer, 1, len, f); 
    }
    fclose(f);
    return buffer;
}

char* generateHash(void* data, size_t len) {
    unsigned char* md5 = MD5(data, len, NULL);
    char* hash = malloc(33); 
    for (int i = 0; i < 16; i++)
        sprintf(hash + (i * 2), "%02x", (unsigned int)md5[i]);
    return hash;    
}

