class Tracker():
	"""Draw attention to a specific part of the screen"""
	def __init__(self, aCanvas):
		self.myCanvas = aCanvas
		self.tag = self.myCanvas.create_oval((0, 0), (32, 32), outline = '')

	def goto(self, x, y):
		"""Goes to given position"""
		self.myCanvas.itemconfigure(self.tag, outline = '#ffffff')
		self.myCanvas.coords(self.tag, x - 16, y - 16, x + 16, y + 16)

	def hide(self):
		"""Disappears"""
		self.myCanvas.itemconfigure(self.tag, outline = '')

