    <button onclick="printInvoice()">Udskriv Faktura</button>

    <div id="modal">
        <div id="modalContent">
            <p>Vil du udskrive fakturaen?</p>
            <button onclick="confirmPrint(true)">ja</button>
            <button onclick="confirmPrint(false)">nej</button>
        </div>
    </div>

    <script>
        function printInvoice() {
            document.getElementById('modal').style.display = 'flex';
        }

        function confirmPrint(isPrint) {
