    let mut blocks = Vec::new();
    let mut current_byte = 0;
    let mut bit_count = 0;

    for &bit in bits {
        current_byte <<= 1; // Shift left
        current_byte |= bit; // Add the current bit
        bit_count += 1;

        if bit_count == 8 {
            blocks.push(current_byte);
            current_byte = 0;
            bit_count = 0;
        }
    }

    if bit_count > 0 {
        current_byte <<= (8 - bit_count); // Padding for remaining bits
        blocks.push(current_byte);
    }

    blocks
}

