def parse_to_8_bit_blocks(bits):
    blocks = []
    current_byte = 0
    bit_count = 0

    for bit in bits:
        current_byte <<= 1  # Shift left
        current_byte |= bit  # Add the current bit
        bit_count += 1

        if bit_count == 8:
            blocks.append(current_byte)
            current_byte = 0
            bit_count = 0

