void calculateMD5(const char* filepath, unsigned char output[MD5_DIGEST_LENGTH]) {
    FILE* file = fopen(filepath, "rb");
    MD5_CTX md5;
    MD5_Init(&md5);

    unsigned char buffer[BUFSIZE];
    size_t bytes;
    while ((bytes = fread(buffer, 1, BUFSIZE, file))) {
        MD5_Update(&md5, buffer, bytes);
    }
    MD5_Final(output, &md5);
    fclose(file);
}

void scanDirectory(const char* dir) {
