public class DungeonVisualizer {
    private static final int WIDTH = 20;
    private static final int HEIGHT = 20;
    private char[][] grid = new char[HEIGHT][WIDTH];

    // Initialize empty grid
    public DungeonVisualizer() {
        for (int i = 0; i < HEIGHT; i++) {
            for (int j = 0; j < WIDTH; j++) {
                grid[i][j] = '.'; // Empty space
            }
        }
    }

    public void addRoom(Room room) {
        for (int i = room.y; i < room.y + room.height; i++) {
            for (int j = room.x; j < room.x + room.width; j++) {
                grid[i][j] = 'R'; // Represent room
            }
        }
        for (Door door : room.doors) {
            grid[door.y][door.x] = 'D'; // Represent door
        }
    }

    public void printDungeon() {
        for (int i = 0; i < HEIGHT; i++) {
            for (int j = 0; j < WIDTH; j++) {
                System.out.print(grid[i][j]);
            }
            System.out.println();
        }
    }

    public static void main(String[] args) {
        DungeonVisualizer visualizer = new DungeonVisualizer();
        Room room1 = new Room(5, 5, 4, 4); // Example room
        room1.addDoor(new Door(5, 7)); // Door on room1
        visualizer.addRoom(room1);

