```python
# app.py
from flask import Flask, request, jsonify
from flask_pymongo import PyMongo
from flask_cors import CORS
import os

app = Flask(__name__)
CORS(app)

app.config["MONGO_URI"] = "mongodb://localhost:27017/noteImageDB"
mongo = PyMongo(app)

@app.route('/api/signup', methods=['POST'])
def signup():
    # Handle Google sign in here (not implemented in this example)
    return jsonify({"msg": "Signup/Sign in successful."})

@app.route('/api/notes', methods=['GET', 'POST'])
def notes():
    if request.method == 'POST':
        note = request.json
        mongo.db.notes.insert_one(note)
        return jsonify(note), 201
    else:
        user_notes = list(mongo.db.notes.find())
        return jsonify(user_notes)

@app.route('/api/images', methods=['GET', 'POST'])
def images():
    if request.method == 'POST':
        image = request.files['image']
        filename = image.filename
        image_path = os.path.join('uploads', filename)
        image.save(image_path)
        mongo.db.images.insert_one({'filename': filename})
        return jsonify({"msg": "Image uploaded successfully."}), 201
    else:
        user_images = list(mongo.db.images.find())
        return jsonify(user_images)

if __name__ == '__main__':
    # Create uploads directory if it doesn't exist
    os.makedirs('uploads', exist_ok=True)
    app.run(debug=True)
```

