            for (int x = 0; x < DUNGEON_WIDTH; x++) {
                map[y][x] = ' ';
            }
        }
    }

    void addWall(Wall wall) {
        walls.add(wall);
    }

    boolean generateMap() {
        while (!walls.isEmpty()) {
            int wallIndex = random.nextInt(walls.size());
            Wall selectedWall = walls.remove(wallIndex);

            int doorX = random.nextInt(selectedWall.x2 - selectedWall.x1) + selectedWall.x1;
            int doorY = random.nextInt(selectedWall.y2 - selectedWall.y1) + selectedWall.y1;
            Door wallDoor = new Door(doorX, doorY);

            int roomWidth = random.nextInt(10) + 5;
            int roomHeight = random.nextInt(10) + 5;
            Room newRoom = new Room(doorX - roomWidth / 2, doorY - roomHeight / 2, roomWidth, roomHeight);
            newRoom.addDoor(wallDoor);

            int attempts = 0;
            while (overlapsExistingRooms(newRoom) && attempts < maxAttempts) {
                newRoom.x += random.nextInt(3) - 1;
                newRoom.y += random.nextInt(3) - 1;
                attempts++;
            }

            if (attempts < maxAttempts) {
                rooms.add(newRoom);
                placeRoomOnMap(newRoom);
                placeDoorOnMap(wallDoor);
            } else {
                return false;
            }
        }
        return true;
    }

    private boolean overlapsExistingRooms(Room newRoom) {
        for (Room existingRoom : rooms) {
            if (existingRoom.overlaps(newRoom)) {
                return true;
            }
        }
        return false;
    }

    private void placeRoomOnMap(Room room) {
        for (int y = room.y; y < room.y + room.height; y++) {
            for (int x = room.x; x < room.x + room.width; x++) {
                if (y >= 0 && y < DUNGEON_HEIGHT && x >= 0 && x < DUNGEON_WIDTH) {
                    map[y][x] = 'R'; // Place the room on the map
                }
            }
        }
    }

    private void placeDoorOnMap(Door door) {
        if (door.x >= 0 && door.x < DUNGEON_WIDTH && door.y >= 0 && door.y < DUNGEON_HEIGHT) {
            map[door.y][door.x] = 'D'; // Place the door on the map
        }
    }

    public void printMap() {
        // Print the map with walls
        for (int y = 0; y < DUNGEON_HEIGHT; y++) {
            for (int x = 0; x < DUNGEON_WIDTH; x++) {
