        self.vocab[self.pad_token] = len(self.vocab)

    def encode(self, sentence):
        return [self.vocab.get(word, self.vocab[self.unk_token]) for word in sentence.split()]

    def decode(self, tokens):
        return ' '.join([self.vocab_inv.get(token, self.unk_token) for token in tokens])

# ----- Transformer Model -----
