    const dp = new Array(N + 1).fill(Infinity);
    dp[0] = dp[1] = 0;

    for (let i = 2; i <= N; i++) {
        dp[i] = dp[i - 1] + 1; // Subtract 1 operation
        if (i % 2 === 0) dp[i] = Math.min(dp[i], dp[i / 2] + 1); // Divide by 2
