#include <iostream>
#include <vector>
#include <algorithm>

int minOperations(int N) {
    std::vector<int> dp(N + 1, 0);

    for (int i = 2; i <= N; ++i) {
        dp[i] = dp[i - 1] + 1; // Subtract 1
        if (i % 2 == 0) {
            dp[i] = std::min(dp[i], dp[i / 2] + 1); // Divide by 2
        }
        if (i % 3 == 0) {
            dp[i] = std::min(dp[i], dp[i / 3] + 1); // Divide by 3
        }
    }

    return dp[N];
}

int main() {
