    current_action = story['start']

    while True:
        print(current_action)

        for action in story['actions']:
            print(f"{action['id']}: {action['description']}")

        choice = input("What do you want to do? (or type 'quit' to exit) ")

        if choice == 'quit':
            print(story['end'])
            break
        elif choice in [str(action['id']) for action in story['actions']]:
            action_index = int(choice) - 1
            current_action = story['actions'][action_index]['results']
            if current_action == story['actions'][3]['results']:
                print(story['end'])
                break
        elif choice.startswith("go to "):
            mod_file = choice[6:].strip()  # What's after "go to "
            if os.path.exists(mod_file):
                story = load_story(mod_file)
                current_action = story['start']
            else:
                print("File does not exist. Please try again.")
        else:
            print("Invalid choice. Please try again.\n")

if __name__ == "__main__":
