```c
#include <stdio.h>
#include <dirent.h>
#include <string.h>
#include <stdlib.h>
#include <sys/stat.h>
#include <sys/types.h>
#include <openssl/md5.h>

#define BUFSIZE 1024
#define TABLE_SIZE 100

typedef struct {
    char* key;  // MD5 hash
    char* value; // filename
} pair;

pair* hashtable[TABLE_SIZE];

int hash(char* str) {
    unsigned long hash = 5381; 
    int c;
    while ((c = *str++)) {
        hash = ((hash << 5) + hash) + c; 
    }
    return hash % TABLE_SIZE;
}

void insert(char* key, char* value) {
    int index = hash(key);
    pair* p = malloc(sizeof(pair));
    p->key = strdup(key); 
    p->value = strdup(value);
    hashtable[index] = p;
}

void freePairs() {
    for (int i = 0; i < TABLE_SIZE; i++) {
        if (hashtable[i]) {
            free(hashtable[i]->key);
            free(hashtable[i]->value);
            free(hashtable[i]);
        }
    }
}

char* generateMD5Hash(unsigned char* data, size_t len) {
    unsigned char digest[MD5_DIGEST_LENGTH];
    MD5(data, len, digest);
    char* hash = malloc(33);
    for (int i = 0; i < MD5_DIGEST_LENGTH; i++)
        sprintf(hash + (i * 2), "%02x", digest[i]);
    return hash;
}

void scanDirectory(const char* dir) {
    DIR* d = opendir(dir);
    struct dirent* de; 

    if (!d) {
        perror("opendir");
        return;
    }

    while ((de = readdir(d)) != NULL) {
        if (de->d_type == DT_REG) {
            char filepath[BUFSIZE];
            snprintf(filepath, sizeof(filepath), "%s/%s", dir, de->d_name);
            struct stat fileStat;
            if (stat(filepath, &fileStat) == 0) {
                unsigned char* fileData = malloc(fileStat.st_size);
                FILE* f = fopen(filepath, "rb");
                fread(fileData, 1, fileStat.st_size, f);
