Here’s a complete C program that scans a directory, generates MD5 hashes for each file, and stores the hashes and filenames in a simple key-value store without leading to warnings when compiled with the `-Wall` flag:

```c
#include <stdio.h>
#include <dirent.h>
#include <stdlib.h>
#include <string.h>
#include <sys/stat.h>
#include <openssl/md5.h>

#define HASH_TABLE_SIZE 100

typedef struct {
    char *key;
    char *value;
} HashEntry;

HashEntry *hashTable[HASH_TABLE_SIZE];

unsigned long hashFunction(const char *str) {
    unsigned long hash = 5381;
    int c;

    while ((c = *str++)) {
        hash = ((hash << 5) + hash) + c; // hash * 33 + c
    }
    return hash % HASH_TABLE_SIZE;
}

void insert(const char *key, const char *value) {
    unsigned long index = hashFunction(key);
    if (hashTable[index] != NULL) {
        free(hashTable[index]->value); // Free previous value to avoid memory leak
    } else {
        hashTable[index] = malloc(sizeof(HashEntry));
    }
    hashTable[index]->key = strdup(key);
    hashTable[index]->value = strdup(value);
}

void freeHashTable() {
    for (int i = 0; i < HASH_TABLE_SIZE; i++) {
        if (hashTable[i] != NULL) {
            free(hashTable[i]->key);
            free(hashTable[i]->value);
            free(hashTable[i]);
        }
    }
}

char* computeMD5(const char *filename) {
    unsigned char data[1024];
    unsigned char hash[MD5_DIGEST_LENGTH];
    MD5_CTX mdContext;
    size_t bytesRead;

    FILE *file = fopen(filename, "rb");
    if (!file) return NULL;

    MD5_Init(&mdContext);
    while ((bytesRead = fread(data, 1, sizeof(data), file)) != 0) {
        MD5_Update(&mdContext, data, bytesRead);
    }
    MD5_Final(hash, &mdContext);
    fclose(file);

    char *hashString = malloc(2 * MD5_DIGEST_LENGTH + 1);
    for (int i = 0; i < MD5_DIGEST_LENGTH; i++) {
        sprintf(&hashString[i * 2], "%02x", hash[i]);
    }
    return hashString;
}

void scanDirectory(const char *dirPath) {
    DIR *dir = opendir(dirPath);
    if (!dir) {
        perror("opendir");
        return;
    }

    struct dirent *entry;
    while ((entry = readdir(dir)) != NULL) {
        if (entry->d_type == DT_REG) { // Only regular files
            char filePath[1024];
            snprintf(filePath, sizeof(filePath), "%s/%s", dirPath, entry->d_name);
            char *hash = computeMD5(filePath);
            if (hash) {
                insert(hash, filePath);
                free(hash);
            }
        }
    }
    closedir(dir);
}

