$result = $conn->query($sql);

// Check and fetch results
if ($result->num_rows > 0) {
    while($row = $result->fetch_assoc()) {
        echo "id: " . $row["id"] . " - Name: " . $row["name"] . "<br>"; // modify as needed
    }
