import javax.swing.*;
import java.awt.*;
import java.awt.image.BufferedImage;

public class DepthMapVisualizer {
    public static void visualizeDepthMap(float[][] depthMap) {
        int width = depthMap[0].length;
        int height = depthMap.length;
        BufferedImage image = new BufferedImage(width, height, BufferedImage.TYPE_INT_RGB);

        // Normalize depthMap to RGB range
        float minDepth = Float.MAX_VALUE, maxDepth = Float.MIN_VALUE;
        for (float[] row : depthMap) {
            for (float val : row) {
                if (val < minDepth) minDepth = val;
                if (val > maxDepth) maxDepth = val;
            }
        }

        for (int y = 0; y < height; y++) {
            for (int x = 0; x < width; x++) {
                int colorValue = (int) (255 * (depthMap[y][x] - minDepth) / (maxDepth - minDepth));
                colorValue = Math.max(0, Math.min(255, colorValue)); // Clamp values
                image.setRGB(x, y, new Color(colorValue, colorValue, colorValue).getRGB());
            }
        }

        JFrame frame = new JFrame("Depth Map");
        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        frame.setSize(width, height);
        frame.add(new JLabel(new ImageIcon(image)));
        frame.setVisible(true);
    }
}
