import React, { useEffect } from 'react';

const DepthMapVisualizer = ({ depthMap }) => {
    useEffect(() => {
        const canvas = document.getElementById('depthCanvas');
        const ctx = canvas.getContext('2d');
        const width = depthMap[0].length;
        const height = depthMap.length;

        canvas.width = width;
        canvas.height = height;

        // Normalize depthMap values
        let minDepth = Math.min(...depthMap.flat());
        let maxDepth = Math.max(...depthMap.flat());

        for (let y = 0; y < height; y++) {
            for (let x = 0; x < width; x++) {
                const value = depthMap[y][x];
                const colorValue = ((value - minDepth) / (maxDepth - minDepth)) * 255;
                const clampedValue = Math.max(0, Math.min(255, colorValue));

                ctx.fillStyle = `rgb(${clampedValue}, ${clampedValue}, ${clampedValue})`;
                ctx.fillRect(x, y, 1, 1);
            }
        }
    }, [depthMap]);

    return <canvas id="depthCanvas" />;
};

export default DepthMapVisualizer;
```

