#include <stdio.h>
#include <pthread.h>
#include <semaphore.h>
#include <unistd.h>

#define MAX 10

int count = 0;
sem_t sem;
pthread_mutex_t mutex;

void* producer(void* arg) {
    for (int i = 0; i < MAX; i++) {
        pthread_mutex_lock(&mutex);
        count++;
        printf("Produced: %d\n", count);
        sem_post(&sem); // Signal the consumer
        pthread_mutex_unlock(&mutex);
        sleep(1);
    }
    return NULL;
}

void* consumer(void* arg) {
    for (int i = 0; i < MAX; i++) {
        sem_wait(&sem); // Wait for the producer
        pthread_mutex_lock(&mutex);
        printf("Consumed: %d\n", count);
        pthread_mutex_unlock(&mutex);
    }
    return NULL;
}

int main() {
    pthread_t prod, cons;
    sem_init(&sem, 0, 0); // Initialize semaphore
    pthread_mutex_init(&mutex, NULL); // Initialize mutex

    pthread_create(&prod, NULL, producer, NULL); // Create producer thread
    pthread_create(&cons, NULL, consumer, NULL); // Create consumer thread

    pthread_join(prod, NULL); // Wait for producer to finish
    pthread_join(cons, NULL); // Wait for consumer to finish

    sem_destroy(&sem); // Destroy semaphore
    pthread_mutex_destroy(&mutex); // Destroy mutex
    return 0;
}
```

