use std::sync::{Arc, Mutex, Condvar};
use std::thread;

const MAX: usize = 10;

struct SharedData {
    count: usize,
}

fn main() {
    let data = Arc::new((Mutex::new(SharedData { count: 0 }), Condvar::new()));

    let producer_data = Arc::clone(&data);
    let consumer_data = Arc::clone(&data);

    let producer = thread::spawn(move || {
        for _ in 0..MAX {
            let (lock, cvar) = &*producer_data;
            let mut data = lock.lock().unwrap();
            data.count += 1;
            println!("Produced: {}", data.count);
            cvar.notify_one(); // Signal the consumer
        }
    });

    let consumer = thread::spawn(move || {
        let (lock, cvar) = &*consumer_data;
        for _ in 0..MAX {
            let mut data = lock.lock().unwrap();
            while data.count == 0 { // Wait if nothing to consume
                data = cvar.wait(data).unwrap();
            }
            println!("Consumed: {}", data.count);
            data.count -= 1; 
        }
    });

    producer.join().unwrap();
    consumer.join().unwrap();
}
```

