use std::sync::{Arc, Mutex, Condvar};
use std::thread;

const MAX_COUNT: i32 = 10;

fn main() {
    let count = Arc::new(Mutex::new(0));
    let condvar = Arc::new(Condvar::new());
    let mut handles = vec![];

    for id in 0..2 {
        let count_clone = Arc::clone(&count);
        let condvar_clone = Arc::clone(&condvar);

        let handle = thread::spawn(move || {
            loop {
                let mut num = count_clone.lock().unwrap();
                while *num < MAX_COUNT && *num % 2 != id {
                    num = condvar_clone.wait(num).unwrap();
                }

                if *num >= MAX_COUNT {
                    break;
                }

                println!("Thread {}: count = {}", id, *num);
                *num += 1;

                condvar_clone.notify_all();
            }
        });

        handles.push(handle);
    }

    for handle in handles {
        handle.join().unwrap();
    }
}
```

