import java.util.ArrayList;
import java.util.Random;

class Dungeon {
    ArrayList<Room> rooms; // List of rooms in the dungeon
    ArrayList<Wall> walls; // List of walls in the dungeon
    int maxAttempts;       // Maximum attempts to place a room
    Random random;         // Random number generator

    Dungeon(int maxAttempts) {
        rooms = new ArrayList<>();
        walls = new ArrayList<>();
        this.maxAttempts = maxAttempts;
        random = new Random();
    }

    // Method to add a wall to the dungeon
    void addWall(Wall wall) {
        walls.add(wall);
    }

    // Method to generate the dungeon map
    boolean generateMap() {
        while (!walls.isEmpty()) {
            int wallIndex = random.nextInt(walls.size()); // Select a random wall
            Wall selectedWall = walls.remove(wallIndex);   // Remove that wall from the list

            // Determine a random position for the door on the selected wall
            int doorX = random.nextInt(selectedWall.x2 - selectedWall.x1) + selectedWall.x1;
            int doorY = random.nextInt(selectedWall.y2 - selectedWall.y1) + selectedWall.y1;
            Door wallDoor = new Door(doorX, doorY); // Create the door

            // Create a new room centered around the door
            int roomWidth = random.nextInt(10) + 5;   // Random width (5 to 14)
            int roomHeight = random.nextInt(10) + 5;  // Random height (5 to 14)
            Room newRoom = new Room(doorX - roomWidth / 2, doorY - roomHeight / 2, roomWidth, roomHeight);
            newRoom.addDoor(wallDoor); // Add the door to the room

            int attempts = 0;
            // Try to place the new room without overlaps
            while (overlapsExistingRooms(newRoom) && attempts < maxAttempts) {
                // Slightly shift the room if it overlaps
                newRoom.x += random.nextInt(3) - 1; // Shift room in x
                newRoom.y += random.nextInt(3) - 1; // Shift room in y
                attempts++;
            }

            // If the room was successfully placed, add it to the dungeon
            if (attempts < maxAttempts) {
                rooms.add(newRoom);
            } else {
                return false; // Failed to place room after maximum attempts
            }
        }
        return true; // Successfully generated the dungeon
    }

    // Method to check if the new room overlaps with existing rooms
    private boolean overlapsExistingRooms(Room newRoom) {
        for (Room existingRoom : rooms) {
            if (existingRoom.overlaps(newRoom)) {
                return true; // Found an overlap
            }
        }
        return false; // No overlaps found
    }
}
