public class GrayCode {

    // Function to convert binary to Gray code
    public static int binaryToGray(int num) {
        return num ^ (num >> 1);
    }

    // Function to get the next Gray code sequence
    public static int getNextGrayCode(int grayCode, boolean direction) {
        int binary = grayToBinary(grayCode);
        int nextBinary;

        if (direction) { // Rightward
            nextBinary = binary + 1;
        } else { // Leftward
            nextBinary = binary - 1;
        }

        return binaryToGray(nextBinary);
    }

    // Function to convert Gray code to binary
    private static int grayToBinary(int gray) {
        int binary = gray;
        while (gray > 0) {
            gray >>= 1;
            binary ^= gray;
        }
        return binary;
    }

    public static void main(String[] args) {
        int grayCode = 2; // Example Gray code
        boolean direction = true; // Rightward
        int nextGray = getNextGrayCode(grayCode, direction);
        System.out.println("Next Gray Code: " + nextGray);
    }
}
