public class ColorGrayCode {

    private static final String[] colors = {
            "Red", "Green", "Blue", "Yellow", 
            "Black", "White", "Orange", "Purple", "Teal"
    };

    public static String getGrayCodeColor(int index) {
        int grayIndex = index ^ (index >> 1);
        return colors[grayIndex % colors.length];
    }

