```cpp
#include <iostream>
#include <vector>
#include <bitset>

using namespace std;

enum class NALUnitType {
    SPS = 33,
    PPS = 34,
};

struct SPS {
    int profile_idc;
    int level_idc;
    int max_sub_layers;
    // Add more fields specific to SPS as needed
};

struct PPS {
    int sps_id;
    // Add more fields specific to PPS as needed
};

class Bitstream {
    const vector<uint8_t>& data;
    size_t bitPos;

public:
    Bitstream(const vector<uint8_t>& data) : data(data), bitPos(0) {}

    bool readBit() {
        int bytePos = bitPos / 8;
        int bitOffset = 7 - (bitPos % 8);
        bool bit = (data[bytePos] >> bitOffset) & 1;
        bitPos++;
        return bit;
    }

    int readBits(int count) {
        int value = 0;
        for (int i = 0; i < count; i++) {
            value = (value << 1) | readBit();
        }
        return value;
    }

    int readUE() { // Exponential Golomb code
        int leadingZeroBits = 0;
        while (!readBit()) leadingZeroBits++;
        return (1 << leadingZeroBits) - 1 + readBits(leadingZeroBits);
    }
};

class HEVCParser {
public:
