```rust
fn pack_bits(values: &[u8]) -> Vec<u8> {
    let mut packed = Vec::new();
    let mut accumulator = 0u8;
    let mut bits_collected = 0;

    for &value in values {
        accumulator = (accumulator << 3) | (value & 0b111); // pack 3 bits
        bits_collected += 3;

        while bits_collected >= 8 {
            packed.push(accumulator >> (bits_collected - 8)); // push the top 8 bits
            bits_collected -= 8;
        }
    }

