    FILE* f = fopen(filename, "rb");
    if (!f) return NULL;

    fseek(f, 0, SEEK_END);
    *length = ftell(f);
    fseek(f, 0, SEEK_SET);

    unsigned char* buffer = malloc(*length);
    fread(buffer, 1, *length, f);
    fclose(f);
    return buffer;
}

char* generateHash(const unsigned char* data, size_t len) {
    unsigned char md5[MD5_DIGEST_LENGTH];
    MD5(data, len, md5);
    char* hash = malloc(33);
    for (int i = 0; i < MD5_DIGEST_LENGTH; i++)
        sprintf(hash + (i * 2), "%02x", md5[i]);
    hash[32] = '\0'; // Null-terminate the string
    return hash;    
}

void scanDirectory(const char* dir) {
    DIR* d = opendir(dir);
    struct dirent* de; 

    if (!d) {
        perror("Unable to open directory");
        return;
    }

    while ((de = readdir(d)) != NULL) {
        if (de->d_type == DT_REG) { // If it's a regular file
            char filepath[BUFSIZE];
            snprintf(filepath, sizeof(filepath), "%s/%s", dir, de->d_name);

            size_t length;
            unsigned char* data = readFile(filepath, &length);
            if (data) {
                char* hash = generateHash(data, length);
                insert(hash, filepath);
                free(data); // Don't forget to free data
            }
        }
    }
    closedir(d);
}

