```c
#include <stdio.h>
#include <stdlib.h>

int main() {
    FILE *f = fopen("file.txt", "rb");
    if (f == NULL) {
        printf("Error opening file");
        exit(1);
    }

    char c;
    while ((c = fgetc(f)) != EOF) {
        for (int i=7; i>=0; i--) {
            printf("%d", (c >> i) & 1);
        }
        printf(" ");
    }
    printf("\n");
    fclose(f);
} 
