```javascript
const fs = require("fs");

function minOperations(N) {
    const dp = Array(N + 1).fill(Infinity);
    dp[1] = 0; // Base case: 0 operations to reduce 1 to 1

    for (let i = 2; i <= N; i++) {
        // Subtract 1
        dp[i] = dp[i - 1] + 1;

        // Divide by 2 if divisible
        if (i % 2 === 0) {
            dp[i] = Math.min(dp[i], dp[i / 2] + 1);
        }

        // Divide by 3 if divisible
        if (i % 3 === 0) {
            dp[i] = Math.min(dp[i], dp[i / 3] + 1);
        }
    }

    return dp[N];
}

const input = parseInt(fs.readFileSync("/dev/stdin", "utf8").trim(), 10);
const result = minOperations(input);
console.log(result);
```

