#include <sys/stat.h>
#include <openssl/md5.h>

#define HASHTABLE_SIZE 100

typedef struct {
    char* key; 
    char* value;
} Pair;

Pair* hashtable[HASHTABLE_SIZE] = {NULL};

int hash(char* str) {
    int hash = 0;
    for (int i = 0; str[i]; i++) {
        hash += str[i]; 
        hash = (hash * 31) % HASHTABLE_SIZE; // simple hash
    }   
    return hash;
}

void insert(char* key, char* value) {
    int index = hash(key);
    Pair* p = malloc(sizeof(Pair));
    p->key = strdup(key);
    p->value = strdup(value);
    hashtable[index] = p;
}

void generateHash(const char* filename, unsigned char* result) {
    unsigned char data[BUFSIZE];
    MD5_CTX ctx;
    MD5_Init(&ctx);

    FILE* f = fopen(filename, "rb");
    if (f == NULL) return;

    size_t bytesRead;
    while ((bytesRead = fread(data, 1, sizeof(data), f)) != 0) {
        MD5_Update(&ctx, data, bytesRead);
    }
    MD5_Final(result, &ctx);
    fclose(f);
}

void scanDirectory(const char* dir) {
    DIR* d = opendir(dir);
    struct dirent* de;
    struct stat fileStat;

    if (d == NULL) return;

    while ((de = readdir(d)) != NULL) {
        if (strcmp(de->d_name, ".") == 0 || strcmp(de->d_name, "..") == 0) continue;

        char path[BUFSIZE];
        snprintf(path, sizeof(path), "%s/%s", dir, de->d_name);

        if (stat(path, &fileStat) == 0) {
            if (S_ISDIR(fileStat.st_mode)) {
                scanDirectory(path); // Recurse into subdirectory
            } else if (S_ISREG(fileStat.st_mode)) {
                unsigned char hash[MD5_DIGEST_LENGTH];
                generateHash(path, hash);
                char hashString[33];
                for (int i = 0; i < 16; i++) {
                    sprintf(&hashString[i * 2], "%02x", hash[i]);
                }
                insert(hashString, path);
            }
        }
    }
    closedir(d);
}

void printHashTable() {
    for (int i = 0; i < HASHTABLE_SIZE; i++) {
        if (hashtable[i]) {
            printf("Hash: %s, Filename: %s\n", hashtable[i]->key, hashtable[i]->value);
        }
    }
}

int main(int argc, char* argv[]) {
    if (argc < 2) {
        printf("Usage: %s <directory>\n", argv[0]);
        return 1;
    }
    scanDirectory(argv[1]);
    printHashTable();
    return 0;
}
```

