public class ExtractField
{
    public static string GetField(string input, int n, char delimiter)
    {
        string[] fields = input.Split(delimiter);
        if (n < 0 || n >= fields.Length)
        {
            return null; // or throw an exception based on your requirements
        }
        return fields[n];
    }
}

