```c++
#include <iostream>
#include <vector>
using namespace std;

// Function to compute the minimum calculations to reduce n to 0
int get_minimum_calculations(int n, vector<int>& memo) {
    // Base case: if n is 0, no calculations needed
    if (n == 0) return 0;

    // If we already computed this state, return the cached result
    if (memo[n] != -1) return memo[n];

    // Initialize the minimum calculations needed to a large value
    int min_calculations = INT_MAX;

    // Option 1: If n is greater than 1, reduce n by 1
    if (n > 1) {
        min_calculations = min(min_calculations, 1 + get_minimum_calculations(n - 1, memo));
    }

    // Option 2: If n is even, reduce n by half
    if (n % 2 == 0) {
        min_calculations = min(min_calculations, 1 + get_minimum_calculations(n / 2, memo));
    }

    // Option 3: If n is divisible by 3, reduce n by a third
    if (n % 3 == 0) {
        min_calculations = min(min_calculations, 1 + get_minimum_calculations(n / 3, memo));
    }

    // Store the result in the memoization array and return it
    return memo[n] = min_calculations;
}

int main() {
    // Prompt user for input
    int n;
    cout << "Enter a positive integer: ";
    cin >> n;

    // Create a memoization vector initialized with -1
    vector<int> memo(n + 1, -1);

    // Calculate and display the minimum calculations needed to reduce n to 0
    cout << "Minimum calculations to reduce " << n << " to 0: "
         << get_minimum_calculations(n, memo) << endl;

    return 0;
}
```

