#include <stdio.h>
#include <dirent.h>
#include <string.h>
#include <stdlib.h>
#include <sys/stat.h>
#include <sys/types.h>
#include <openssl/md5.h>

#define BUFSIZE 4096

typedef struct {
    char* key; 
    char* value;
} pair;

pair* hashtable[10000];  // Adjust size as needed.

int hash(char* str) {
    int hash = 0;
    int len = strlen(str);
    for (int i = 0; i < len; i++) {
        hash = (hash * 31 + str[i]) % 10000;
    }   
    return hash;
}

void insert(char* key, char* value) {
    int index = hash(key);
    while (hashtable[index] != NULL) {  // Collision resolution
        index = (index + 1) % 10000;
    }
    pair* p = malloc(sizeof(pair));
    p->key = strdup(key);
    p->value = strdup(value);
    hashtable[index] = p;
}

void calculateMD5(FILE* file, unsigned char* result) {
    MD5_CTX ctx;
    MD5_Init(&ctx);

    unsigned char buffer[BUFSIZE];
    size_t bytesRead;

    while ((bytesRead = fread(buffer, 1, BUFSIZE, file)) != 0) {
        MD5_Update(&ctx, buffer, bytesRead);
    }

    MD5_Final(result, &ctx);
}

char* generateHash(const char* filename) {
    unsigned char digest[MD5_DIGEST_LENGTH];
    FILE* file = fopen(filename, "rb");
    if (file == NULL) return NULL;

    calculateMD5(file, digest);
    fclose(file);

    char* hash = malloc(33);
    for (int i = 0; i < MD5_DIGEST_LENGTH; i++)
        sprintf(hash + (i * 2), "%02x", digest[i]);

    return hash;
}

void scanDirectory(const char* dir) {
    struct dirent* entry;
    DIR* dp = opendir(dir);

    if (!dp) return;

    while ((entry = readdir(dp)) != NULL) {
        if (entry->d_name[0] == '.')
            continue;  // Skip hidden files and directories

        char path[1024];
        snprintf(path, sizeof(path), "%s/%s", dir, entry->d_name);

        if (entry->d_type == DT_DIR) {
            scanDirectory(path);  // Recursion
        } else {
            char* hash = generateHash(path);
            if (hash) {
                insert(hash, path);
                printf("Hash: %s => File: %s\n", hash, path);
                free(hash);
            }
        }
    }
    closedir(dp);
}

int main(int argc, char* argv[]) {
    if (argc != 2) {
        printf("Usage: %s <directory>\n", argv[0]);
        return 1;
    }

    scanDirectory(argv[1]);

    // Free hashtable memory
    for (int i = 0; i < 10000; i++) {
        if (hashtable[i]) {
            free(hashtable[i]->key);
            free(hashtable[i]->value);
            free(hashtable[i]);
        }
    }

    return 0;
}
