    circleOffset = Offset(
      random.nextDouble() * (MediaQuery.of(context).size.width - circleSize),
      random.nextDouble() * (MediaQuery.of(context).size.height - circleSize),
    );
    setState(() {});
  }

  void _incrementScore() {
    score++;
    _spawnCircle();
  }

  @override
  void initState() {
    super.initState();
    _spawnCircle();
  }

  @override
  Widget build(BuildContext context) {
