#include <iostream>
#include <vector>
#include <cstdint>

enum class NALUnitType {
    SPS = 32,
    PPS = 33,
};

struct NALUnit {
    NALUnitType type;
    std::vector<uint8_t> data;
};

class HEVCParser {
public:
    std::vector<NALUnit> parse(const std::vector<uint8_t>& data) {
        std::vector<NALUnit> nalUnits;
        size_t i = 0;

        while (i < data.size() - 3) {
            // Search for NAL unit start code (0x00000001 or 0x000001)
            if (data[i] == 0 && data[i + 1] == 0 && data[i + 2] == 0 && data[i + 3] == 1) {
                i += 4; // move past the start code
            } else if (data[i] == 0 && data[i + 1] == 0 && data[i + 2] == 1) {
                i += 3; // move past the start code
            } else {
                ++i;
                continue; // not a start code
            }

            // Get NAL unit type
            uint8_t nalType = data[i] & 0x3F; // 6 bits for NAL type
            NALUnitType nalUnitType;
            if (nalType == 32) nalUnitType = NALUnitType::SPS;
            else if (nalType == 33) nalUnitType = NALUnitType::PPS;
            else {
                ++i; // move to the next byte
                continue; // not SPS or PPS
            }

            // Find length of NAL unit (Assuming the rest of the NAL unit can be read until the next start code)
            size_t start = i;
            while (i < data.size() && !(data[i] == 0 && data[i + 1] == 0 && data[i + 2] == 0 && data[i + 3] == 1) &&
                   !(data[i] == 0 && data[i + 1] == 0 && data[i + 2] == 1)) {
                ++i;
            }
            size_t nalLength = i - start;

            // Store the NAL unit
            NALUnit nalUnit{nalUnitType, std::vector<uint8_t>(data.begin() + start, data.begin() + start + nalLength)};
            nalUnits.push_back(nalUnit);
        }
        return nalUnits;
    }
};

