public static ReadOnlySpan<char> GetField(ReadOnlySpan<char> input, int n, char delimiter)
{
    int currentField = 0;
    int start = 0;

    while (start < input.Length)
    {
        int end = start;

        // Find the end of the current field
        while (end < input.Length && input[end] != delimiter)
        {
            end++;
        }

        // If the current field matches n, return it
        if (currentField == n)
        {
            return input.Slice(start, end - start);
        }

        // Move to the next field
        start = end + 1; // Skip the delimiter
        currentField++;
    }

    return ReadOnlySpan<char>.Empty; // Return an empty span if n is out of range
}
