function minOperations(n) {
    const dp = Array(n + 1).fill(Infinity);
    dp[0] = 0;

    for (let i = 1; i <= n; i++) {
        // Add 1 operation
        dp[i] = dp[i - 1] + 1;

        // Double operation (if applicable)
        if (i % 2 === 0) {
            dp[i] = Math.min(dp[i], dp[i / 2] + 1);
        }
    }

    return dp[n];
}

