const BLOCK_SIZE: usize = 8;
const BIT_SIZE: usize = 3;

fn pack_bits(bits: &[u8]) -> Vec<u8> {
    let mut packed = Vec::new();
    let mut accumulator = 0;
    let mut current_bit = 0;

    for &bit in bits {
        accumulator <<= BIT_SIZE; // Shift left by BIT_SIZE
        accumulator |= (bit & 0b111); // Mask to ensure 3 bits
        current_bit += BIT_SIZE;

        if current_bit >= BLOCK_SIZE {
            packed.push(accumulator);
            accumulator = 0;
            current_bit = 0;
        }
    }

    // Add any remaining bits
    if current_bit > 0 {
        packed.push(accumulator);
    }

    packed
}

fn main() {
