Here's a complete C program that lists all files in a directory, computes their MD5 hashes, and stores the filenames and their corresponding hashes in a hash table.

```c
#include <stdio.h>
#include <stdlib.h>
#include <dirent.h>
#include <string.h>
#include <sys/stat.h>
#include <openssl/md5.h>

#define HASH_TABLE_SIZE 1024

typedef struct {
    char* key;   // MD5 hash
    char* value; // Filename
    struct Entry* next;
} Entry;

Entry* hashTable[HASH_TABLE_SIZE] = {NULL};

unsigned long hash(const char* str) {
    unsigned long hash = 5381;
    int c;
    while ((c = *str++)) {
        hash = ((hash << 5) + hash) + c;
    }
    return hash % HASH_TABLE_SIZE;
}

void insert(const char* key, const char* value) {
    unsigned long index = hash(key);
    Entry* newEntry = malloc(sizeof(Entry));
    newEntry->key = strdup(key);
    newEntry->value = strdup(value);
    newEntry->next = hashTable[index];
    hashTable[index] = newEntry;
}

void freeHashTable() {
    for (int i = 0; i < HASH_TABLE_SIZE; i++) {
        Entry* entry = hashTable[i];
        while (entry) {
            Entry* temp = entry;
            entry = entry->next;
            free(temp->key);
            free(temp->value);
            free(temp);
        }
    }
}

void computeMD5(const char* filename, unsigned char* result) {
    FILE* file = fopen(filename, "rb");
    if (!file) return;

    MD5_CTX ctx;
    MD5_Init(&ctx);

    unsigned char buffer[1024];
    size_t bytesRead;
    while ((bytesRead = fread(buffer, 1, sizeof(buffer), file)) != 0) {
        MD5_Update(&ctx, buffer, bytesRead);
    }

    MD5_Final(result, &ctx);
    fclose(file);
}

void scanDirectory(const char* dirPath) {
    struct dirent* entry;
    DIR* dir = opendir(dirPath);
    if (!dir) return;

    while ((entry = readdir(dir)) != NULL) {
        if (entry->d_type == DT_REG) { // Regular file
            char filepath[1024];
            snprintf(filepath, sizeof(filepath), "%s/%s", dirPath, entry->d_name);

            unsigned char hash[MD5_DIGEST_LENGTH];
            computeMD5(filepath, hash);

            char hashString[MD5_DIGEST_LENGTH * 2 + 1];
            for (int i = 0; i < MD5_DIGEST_LENGTH; i++)
                sprintf(&hashString[i * 2], "%02x", hash[i]);

            insert(hashString, filepath);
            printf("File: %s, MD5: %s\n", filepath, hashString);
        } else if (entry->d_type == DT_DIR && strcmp(entry->d_name, ".") && strcmp(entry->d_name, "..")) {
            char subDirPath[1024];
            snprintf(subDirPath, sizeof(subDirPath), "%s/%s", dirPath, entry->d_name);
            scanDirectory(subDirPath);
        }
    }
    closedir(dir);
}

int main(int argc, char* argv[]) {
    if (argc < 2) {
        printf("Usage: %s <directory>\n", argv[0]);
        return 1;
    }

    scanDirectory(argv[1]);
    freeHashTable();
    return 0;
}
```

