            snprintf(filepath, sizeof(filepath), "%s/%s", dirPath, entry->d_name);

            unsigned char hash[MD5_DIGEST_LENGTH];
            computeMD5(filepath, hash);

            char hashString[MD5_DIGEST_LENGTH * 2 + 1];
            for (int i = 0; i < MD5_DIGEST_LENGTH; i++)
                sprintf(&hashString[i * 2], "%02x", hash[i]);

            insert(hashString, filepath);
            printf("File: %s, MD5: %s\n", filepath, hashString);
        } else if (entry->d_type == DT_DIR && strcmp(entry->d_name, ".") && strcmp(entry->d_name, "..")) {
            char subDirPath[1024];
            snprintf(subDirPath, sizeof(subDirPath), "%s/%s", dirPath, entry->d_name);
            scanDirectory(subDirPath);
        }
    }
    closedir(dir);
}

int main(int argc, char* argv[]) {
    if (argc < 2) {
        printf("Usage: %s <directory>\n", argv[0]);
        return 1;
    }

    scanDirectory(argv[1]);
    freeHashTable();
    return 0;
}
```

