use std::fs::File;
use std::io::{self, Read};

fn print_bits(file: &mut File) -> io::Result<()> {
    let mut buffer = [0u8; 1];
    while file.read(&mut buffer)? > 0 {
        let byte = buffer[0];
        for i in (0..8).rev() {
            print!("{}", (byte >> i) & 1);
        }
        print!(" "); // Print space between bytes
    }
    println!(); // Print a new line after all bits are printed
    Ok(())
}

fn main() -> io::Result<()> {
    let file_path = "file.txt"; // Replace with your file path
    let mut file = File::open(file_path)?;
    print_bits(&mut file)?;
    Ok(())
}
```

