### Server Code (NamedPipeServer.dpr)
```delphi
program NamedPipeServer;

{$APPTYPE CONSOLE}

uses
  SysUtils, Windows;

const
  PIPE_NAME = '\\.\pipe\MyNamedPipe';

var
  hPipe: THandle;
  buffer: array[0..255] of Char;
  bytesRead: DWORD;
begin
  hPipe := CreateNamedPipe(PIPE_NAME,
                            PIPE_ACCESS_DUPLEX,
                            PIPE_TYPE_MESSAGE or PIPE_READMODE_MESSAGE or PIPE_WAIT,
                            1,
                            256,
                            256,
                            0,
                            nil);

  if hPipe = INVALID_HANDLE_VALUE then
    RaiseLastOSError;

  WriteLn('Waiting for client connection...');
  if ConnectNamedPipe(hPipe, nil) or (GetLastError = ERROR_PIPE_CONNECTED) then
  begin
    ReadFile(hPipe, buffer, SizeOf(buffer), bytesRead, nil);
    WriteLn('Received: ', buffer);
    WriteFile(hPipe, 'Hello from server!', 17, bytesRead, nil);
  end else
    RaiseLastOSError;

  FlushFileBuffers(hPipe);
  DisconnectNamedPipe(hPipe);
  CloseHandle(hPipe);
end.
```

### Client Code (NamedPipeClient.dpr)
```delphi
program NamedPipeClient;

{$APPTYPE CONSOLE}

uses
  SysUtils, Windows;

const
  PIPE_NAME = '\\.\pipe\MyNamedPipe';

var
  hPipe: THandle;
  buffer: array[0..255] of Char;
  bytesWritten: DWORD;
begin
  hPipe := CreateFile(PIPE_NAME,
                      GENERIC_READ or GENERIC_WRITE,
                      0,
                      nil,
                      OPEN_EXISTING,
                      0,
                      0);

  if hPipe = INVALID_HANDLE_VALUE then
    RaiseLastOSError;

  WriteFile(hPipe, 'Hello from client!', 17, bytesWritten, nil);
  ReadFile(hPipe, buffer, SizeOf(buffer), bytesWritten, nil);
  WriteLn('Received: ', buffer);

  CloseHandle(hPipe);
end.
```

