# Tokenizer: Simple whitespace tokenizer
class SimpleTokenizer:
    def __init__(self):
        self.vocab = {}
        self.rev_vocab = {}
        self.token_count = 0

    def fit(self, texts):
        for text in texts:
            for word in text.split():
                if word not in self.vocab:
                    self.vocab[word] = self.token_count
                    self.rev_vocab[self.token_count] = word
                    self.token_count += 1

    def encode(self, text):
        return [self.vocab[word] for word in text.split() if word in self.vocab]

    def decode(self, tokens):
        return ' '.join(self.rev_vocab[token] for token in tokens)

# Dataset class
class TextDataset(Dataset):
    def __init__(self, texts, tokenizer):
        self.texts = texts
        self.tokenizer = tokenizer

    def __len__(self):
        return len(self.texts)

    def __getitem__(self, idx):
        return torch.tensor(self.tokenizer.encode(self.texts[idx]), dtype=torch.long)

# Multi-Head Attention
class MultiHeadAttention(nn.Module):
    def __init__(self, embed_size, heads):
        super(MultiHeadAttention, self).__init__()
        self.heads = heads
        self.embed_size = embed_size
        self.head_dim = embed_size // heads

        assert (
            self.head_dim * heads == embed_size
        ), "Embedding size must be divisible by heads"

        self.values = nn.Linear(embed_size, embed_size, bias=False)
        self.keys = nn.Linear(embed_size, embed_size, bias=False)
        self.queries = nn.Linear(embed_size, embed_size, bias=False)
        self.fc_out = nn.Linear(embed_size, embed_size)

    def forward(self, x):
        N, seq_length, embed_size = x.shape

        # Split into heads
        values = self.values(x).view(N, seq_length, self.heads, self.head_dim)
        keys = self.keys(x).view(N, seq_length, self.heads, self.head_dim)
        queries = self.queries(x).view(N, seq_length, self.heads, self.head_dim)

        values, keys, queries = values.permute(0, 2, 1, 3), keys.permute(0, 2, 1, 3), queries.permute(0, 2, 1, 3)

        energy = torch.einsum("nhqd,nhkd->nhqk", queries, keys)  # (N, heads, query_len, key_len)
        attention = torch.softmax(energy / (self.embed_size ** (1 / 2)), dim=3)

        out = torch.einsum("nhql,nhld->nhqd", attention, values).reshape(N, seq_length, self.heads * self.head_dim)
        return self.fc_out(out)

# Feed Forward Network
class FeedForward(nn.Module):
    def __init__(self, embed_size, ff_size):
        super(FeedForward, self).__init__()
        self.fc1 = nn.Linear(embed_size, ff_size)
        self.fc2 = nn.Linear(ff_size, embed_size)

    def forward(self, x):
        return self.fc2(torch.relu(self.fc1(x)))

# Transformer Block
class TransformerBlock(nn.Module):
    def __init__(self, embed_size, heads, ff_size, dropout):
        super(TransformerBlock, self).__init__()
        self.attention = MultiHeadAttention(embed_size, heads)
        self.norm1 = nn.LayerNorm(embed_size)
        self.norm2 = nn.LayerNorm(embed_size)
        self.feed_forward = FeedForward(embed_size, ff_size)
        self.dropout = nn.Dropout(dropout)

    def forward(self, x):
        attention = self.attention(x)
        x = self.dropout(self.norm1(attention + x))  # Add & Norm
        forward = self.feed_forward(x)
        return self.dropout(self.norm2(forward + x))  # Add & Norm

