import java.net.HttpURLConnection;
import java.net.URL;
import java.util.Scanner;

public class Task {

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        System.out.print("Your name: ");
        String name = sc.nextLine();

        System.out.print("How much money do you have in your bank account (in dollars)? ");
        double dollars = sc.nextDouble();

        double conversionRate = fetchConversionRate(); // Fetch from online service
        double euros = dollars * conversionRate;

        System.out.println("Hello " + name + ", you have " + dollars + " dollars which is " + euros + " euros!");
    }

    private static double fetchConversionRate() {
        try {
            URL url = new URL("https://api.exchangerate-api.com/v4/latest/USD");
            HttpURLConnection conn = (HttpURLConnection) url.openConnection();
            conn.setRequestMethod("GET");
            conn.connect();

            Scanner sc = new Scanner(url.openStream());
            String jsonResponse = sc.useDelimiter("\\Z").next();
            sc.close();

            // Basic JSON parsing (for a simple demo, a library would be better)
            int index = jsonResponse.indexOf("\"EUR\":") + 6;
            int endIndex = jsonResponse.indexOf(",", index);
            return Double.parseDouble(jsonResponse.substring(index, endIndex));
        } catch (Exception e) {
            e.printStackTrace();
            return 0.85; // Default fallback if error occurs
        }
    }
}
```

