void store_3bit_array_in_buffer(uint8_t *buffer, const uint8_t *array, size_t array_size) {
    for (size_t i = 0; i < array_size; i++) {
        buffer[i / 3] |= (array[i] & 0x07) << (i % 3) * 3;
    }
}

