#include <QApplication>
#include <QTabBar>
#include <QWidget>
#include <QVBoxLayout>
#include <QPushButton>

class TabWidget : public QWidget {
    Q_OBJECT

public:
    TabWidget(QWidget *parent = nullptr) : QWidget(parent), commitCount(0) {
        QVBoxLayout *layout = new QVBoxLayout(this);
        tabBar = new QTabBar(this);
        layout->addWidget(tabBar);

        QPushButton *commitButton = new QPushButton("Commit", this);
        layout->addWidget(commitButton);
        connect(commitButton, &QPushButton::clicked, this, &TabWidget::handleCommit);
    }

private slots:
    void handleCommit() {
        commitCount++;

        if (commitCount % 2 == 0) { // Open on every second commit
            addTab();
        }

        if (commitCount % 3 == 0) { // Close on every third commit
            closeTab();
        }
    }

private:
    QTabBar *tabBar;
    int commitCount;

    void addTab() {
        tabBar->addTab(QString("Tab %1").arg(tabBar->count() + 1));
    }

    void closeTab() {
        int tabIndex = tabBar->count() - 1; // Close the last opened tab
        if (tabIndex >= 0) {
            tabBar->removeTab(tabIndex);
        }
    }
};

int main(int argc, char *argv[]) {
    QApplication a(argc, argv);
    TabWidget w;
    w.show();
    return a.exec();
}

#include "main.moc"
```

