#include <iostream>
#include <vector>
#include <algorithm>

int KMP(const std::vector<int>& weights, const std::vector<int>& values, int k) {
    int n = weights.size();
    std::vector<std::vector<int>> dp(n + 1, std::vector<int>(k + 1, 0));

    for(int i = 1; i <= n; ++i) {
        for(int weight = 0; weight <= k; ++weight) {
            if(weights[i - 1] <= weight)
                dp[i][weight] = std::max(dp[i - 1][weight], dp[i - 1][weight - weights[i - 1]] + values[i - 1]);
            else
                dp[i][weight] = dp[i - 1][weight];
        }
    }
    return dp[n][k];
