### Complete C Program

```c
#include <stdio.h> 
#include <dirent.h>
#include <string.h>
#include <stdlib.h>
#include <sys/stat.h>
#include <sys/types.h>
#include <openssl/md5.h>

#define BUFSIZE 1024

typedef struct Node {
    char* key; 
    char* value;
    struct Node* next;
} Node;

Node* hashtable[10000]; // Larger initial size for better distribution

int hash(char* str) {
    int hash = 0;
    int len = strlen(str);
    for (int i = 0; i < len; i++) {
        hash += str[i]; 
        hash = (hash * str[i]) % 10000; // Use larger table size
    }   
    return hash;
}

void insert(char* key, char* value) {
    int index = hash(key);
    Node* newNode = malloc(sizeof(Node));
    newNode->key = strdup(key);
    newNode->value = strdup(value);
    newNode->next = hashtable[index];
    hashtable[index] = newNode;
}

char* readFile(char* filename) {
    FILE* f = fopen(filename, "rb");
    if (!f) return NULL;

    unsigned char* buffer = malloc(BUFSIZE);
    size_t len;
    size_t totalRead = 0;
    unsigned char* data = malloc(BUFSIZE * 100); // Allocate enough space

    while ((len = fread(buffer, 1, BUFSIZE, f)) > 0) {
        memcpy(data + totalRead, buffer, len);
        totalRead += len;
    }
    fclose(f);
    free(buffer);
    data[totalRead] = '\0';
    return data; // Return data for hashing
}

char* generateHash(void* data, size_t len) {
    unsigned char md5[MD5_DIGEST_LENGTH];
    MD5(data, len, md5);
    char* hash = malloc(33);
    for (int i = 0; i < MD5_DIGEST_LENGTH; i++)
        sprintf(hash + (i * 2), "%02x", md5[i]);
    return hash;
}

void scanDirectory(char* dir) {
    DIR* d = opendir(dir);
    struct dirent* de; 

    if (!d) {
        perror("opendir");
        return;
    }

    while ((de = readdir(d)) != NULL) {
        if (de->d_type == DT_REG) { // Check if it is a regular file
            char filepath[1024];
            snprintf(filepath, sizeof(filepath), "%s/%s", dir, de->d_name);
            char* data = readFile(filepath);
            if (data) {
                char* hash = generateHash(data, strlen(data));
                insert(hash, filepath);
                free(data);
                free(hash);
            }
        } else if (de->d_type == DT_DIR && strcmp(de->d_name, ".") != 0 && strcmp(de->d_name, "..") != 0) {
            char subdir[1024];
            snprintf(subdir, sizeof(subdir), "%s/%s", dir, de->d_name);
            scanDirectory(subdir); // Recursive call for subdirectories
        }
    }
    closedir(d);
}

int main(int argc, char* argv[]) {
    if (argc != 2) {
        fprintf(stderr, "Usage: %s <directory>\n", argv[0]);
        return 1;
    }

    scanDirectory(argv[1]); // Directory path specified as command-line argument
    return 0;
}
```

