#include <iostream>
#include <fstream>
#include <string>
#include <cstdlib>

void runMips(const std::string& mipsCode) {
    // Create and write MIPS code to a file
    std::ofstream file("mips_program.asm");
    file << mipsCode;
    file.close();

    // Assemble and link
    system("mips-as mips_program.asm -o mips_program.o");
    system("mips-ld mips_program.o -o my_mips_program");

    // Execute the MIPS program
    system("my_mips_program");

    // TODO: Read and process output if necessary
}

int main() {
