const DepthMapVisualizer = ({ depthMap }) => {
  const data = {
    datasets: [
      {
        label: 'Depth Map',
        data: depthMap.flatMap((row, y) =>
          row.map((depth, x) => ({
            x,
            y,
            z: depth,
          }))
